package com.merico.inftest.exception;import com.google.common.base.Joiner;import org.testng.collections.Lists;import java.util.List;public class AssertExceptionMsg {    public static String genertaMssage(List<String> bodyErr, List<String> keyExistErr, List<String> excludeErr) {        List<String> errMsg = Lists.newArrayList();        errMsg.add("");        if (bodyErr != null && !bodyErr.isEmpty()) {            String bodyErrMsg = Joiner.on("\r\n").join(bodyErr);            errMsg.add("assert.body:");            errMsg.add(bodyErrMsg);        }        if (keyExistErr != null && !keyExistErr.isEmpty()) {            errMsg.add("");            String keyErrMsg = Joiner.on("\r\n").join(keyExistErr);            errMsg.add("assert.keyExist:");            errMsg.add(keyErrMsg);        }        if (excludeErr != null && !excludeErr.isEmpty()) {            errMsg.add("");            String excludeErrMsg = Joiner.on("\r\n").join(excludeErr);            errMsg.add("assert.exclude:");            errMsg.add(excludeErrMsg);        }        return Joiner.on("\r\n").join(errMsg);    }}